        style={styles.checkbox}
        disabled
        checked={checked2}
        onChange={(value: boolean) => onStateChange(value, 2)}
      />
      <CheckBox
        style={styles.checkbox}
        indeterminate={true}
        checked={checked3}
        onChange={(value: boolean) => onStateChange(value, 3)}
      />
    </View>
  );
};

