import React from 'react';
import {
  Icon,
  OverflowMenu,
  TopNavigation,
  TopNavigationAction,
} from 'react-native-ui-kitten';

const BackIcon = (style) => (
  <Icon {...style} name='arrow-back'/>
);

const MenuIcon = (style) => (
  <Icon {...style} name='more-vertical'/>
);

const InfoIcon = (style) => (
  <Icon {...style} name='info'/>
);

const LogoutIcon = (style) => (
  <Icon {...style} name='log-out'/>
);

export class TopNavigationWithMenuShowcase extends React.Component {

  state = {
    menuVisible: false,
  };

  menuData = [
    { title: 'About', icon: InfoIcon },
    { title: 'Logout', icon: LogoutIcon },
  ];

  onMenuActionPress = () => {
    const menuVisible = !this.state.menuVisible;
    this.setState({ menuVisible });
  };

  onMenuItemSelect = (index) => {
    // Handle Item Select
  };

  renderMenuAction = () => (
    <OverflowMenu
      visible={this.state.menuVisible}
      data={this.menuData}
      onSelect={this.onMenuItemSelect}
      onBackdropPress={this.onMenuActionPress}>
      <TopNavigationAction
        icon={MenuIcon}
        onPress={this.onMenuActionPress}
      />
    </OverflowMenu>
  );

  renderBackAction = () => (
    <TopNavigationAction icon={BackIcon} />
  );

  render() {
    return (
      <TopNavigation
        leftControl={this.renderBackAction()}
        rightControls={this.renderMenuAction()}
      />
    );
  }
}

import React from 'react';
import { StyleSheet } from 'react-native';
import {
  ViewPager,
  Layout,
  Text,
} from 'react-native-ui-kitten';

export class ViewPagerInlineStylingShowcase extends React.Component {

  state = {
    selectedIndex: 0,
  };

  onSelect = (selectedIndex) => {
    this.setState({ selectedIndex });
  };

  render() {
    return (
      <ViewPager
        style={styles.container}
        selectedIndex={this.state.selectedIndex}
        onSelect={this.onSelect}>
        <Layout>
          <Text>Tab 1</Text>
        </Layout>
        <Layout>
          <Text>Tab 2</Text>
        </Layout>
      </ViewPager>
    );
  }
}

const styles = StyleSheet.create({
